mapper.select:
SELECT yg.`ygid`,qx.* FROM `yuangong` yg
INNER JOIN `userrole` jsyg
ON yg.`ygid` = jsyg.`ygid`
INNER JOIN role js
ON js.`rid` = jsyg.`rid`
INNER JOIN `rolepermission` cd
ON cd.`rid` = js.`rid`
INNER JOIN `permission` qx
ON qx.`pid`= cd.`pid`


    AND qx.pstatus = '0'

        and  yg.`ygid` = #{ygid}
        AND qx.pisnavi = #{pisnavi}


    and  yg.`ygid` = #{ygid}
    and qx.`parentid`=#{pid}


        and  yg.`ygid` = #{ygid}
        and qx.`parentid`=#{pid}


    ORDER BY qx.pid ASC

SELECT yg.`ygid`,qx.* FROM `yuangong` yg
INNER JOIN `userrole` jsyg
ON yg.`ygid` = jsyg.`ygid`
INNER JOIN role js
ON js.`rid` = jsyg.`rid`
INNER JOIN `rolepermission` cd
ON cd.`rid` = js.`rid`
INNER JOIN `permission` qx
ON qx.`pid`= cd.`pid`

AND qx.pstatus = '0'

    and  yg.`ygid` = #{ygid}
    AND qx.pisnavi = #{pisnavi}


    and  yg.`ygid` = #{ygid}
    AND qx.pisnavi = #{pisnavi}


    and  yg.`ygid` = #{ygid}
    and qx.`parentid`=#{pid}
    AND qx.pisnavi = #{pisnavi}


    and  yg.`ygid` = #{ygid}
    and qx.`parentid`=#{pid}
    AND qx.pisnavi = #{pisnavi}


ORDER BY qx.pid ASC
